const ffmpeg = require('fluent-ffmpeg');
console.log('');

const ffmpegInstaller = require('@ffmpeg-installer/ffmpeg');
console.log('');

ffmpeg.setFfmpegPath(ffmpegInstaller.path);
console.log('');

ffmpeg('./test-long.mp4').addOption([
	'-profile:v baseline',
	'-level 3.0',
	'-start_number 0',
	'-hls_time 10',
	'-hls_list_size 0',
	'-f hls'
]).output('./transcode-result/test.m3u8').on('end', () => {
	console.log('end');
}).run();